for name, cords in lisbeth_cords.items():
    fish_id = big_fish_ids_rev.get(name)
    resp = requests.get(f'{gt_url}/db/doc/item/en/3/{fish_id}.json')
    data = json.loads(resp.text)
    spot = data.get('item').get('fishingSpots')[0]
    gt_zone_mapping[str(spot)]['lisbeth_cords'] = cords

with open("gt_zone_mapping.json", "w") as file:
    json.dump(gt_zone_mapping, file)
